(200, 9, 2, 'Wij wachten met geduld,'),
(200, 9, 3, 'Tot Uwe kracht ons vrijmaakt,'),
(200, 9, 4, 'En Ge onze hoop vervult.'),
(201, 1, 1, 'WAARACHTIG, Heilig, Gij,'),
(201, 1, 2, 'Wij bouwen op Uw Naam,'),
(201, 1, 3, 'Als broederen in Uw liefde, o Heer,'),
(201, 1, 4, 'Eendrachtig hier tezaâm.'),
(201, 2, 1, 'In U is \'s Vaders lust,'),
(201, 2, 2, 'Gezalfde op de troon,'),
(201, 2, 3, 'In wie Zijn liefde eeuwig rust,'),
(201, 2, 4, 'Zijn veelgeliefde Zoon.'),
(201, 3, 1, 'O Eerstgeboor\'ne, Gij!'),
(201, 3, 2, 'Der broederen vreugde, Heer!'),
(201, 3, 3, 'Uw zeker woord erkennen wij;'),
(201, 3, 4, 'Wij buigen voor U neer.'),
(201, 4, 1, 'Geef ons, Uw Naam ter eer,'),
(201, 4, 2, '- Wachtend Uw heerlijkheid -'),
