(203, 2, 1, 'In deze tekenen aanschouwen wij'),
(203, 2, 2, 'Hoe Gij Uw alles gaaft, in liefde vrij;'),
(203, 2, 3, 'O! welk een gave, welk een heerlijkheid!'),
(203, 2, 4, 'Wij zijn met hart en ziel U toegewijd.'),
(203, 3, 1, 'Zo heffen wij, eendrachtig hier tezaâm,'),
(203, 3, 2, 'Een lied van liefde dankbaar tot Uw Naam;'),
(203, 3, 3, 'Ons hart verhoogt U, brengt U heerlijkheid,'),
(203, 3, 4, 'Door Uwe grote liefde, o Heer, bevrijd.'),
(204, 1, 1, 'IN Uw bijzijn, Heer, aanbiddend,'),
(204, 1, 2, 'Buigen wij voor U ons neer;'),
(204, 1, 3, 'Door Uw grootheid diep bewogen,'),
(204, 1, 4, 'Brengen we U eenstemmig eer!'),
(204, 2, 1, 'Zoon van God, Gij hebt verwonnen,'),
(204, 2, 2, 'Ere zij U toegebracht!'),
(204, 2, 3, 'In Uw glorie straalt ons tegen'),
(204, 2, 4, 'Uwe wondere liefde en macht.'),
(204, 3, 1, 'Heer, in Uw Persoon en grootheid'),
(204, 3, 2, 'Hebt Gij volheid, majesteit;'),
(204, 3, 3, 'Eerste Gij in alle dingen,'),
(204, 3, 4, 'Eeuwig straalt Uw heerlijkheid!'),
(204, 4, 1, 'Zingen we U met jubeltonen,'),
(204, 4, 2, 'In de vreugde die Ge ons schenkt!'),
(204, 4, 3, 'Wijl ons dankbaar lied, vol liefde,'),
