(205, 1, 1, 'O JEZUS, Lam van God,'),
(205, 1, 2, 'Ons reddend door genâ,'),
(205, 1, 3, 'Dronkt Gij de bitt\'re kelk des doods'),
(205, 1, 4, 'Op Golgotha.'),
(205, 2, 1, 'Barmhartig Priester Gij!'),
(205, 2, 2, 'Verlosser, Herder, Vrind,'),
(205, 2, 3, 'Gij, die ons tot het einde toe'),
(205, 2, 4, 'Zo trouw bemint.'),
(205, 3, 1, 'Gij zijt der ziele kracht,'),
(205, 3, 2, 'Terwijl Ge als Gids ons leidt,'),
(205, 3, 3, 'Tot wij Uw aanschijn zien, o Heer,'),
(205, 3, 4, 'In heerlijkheid.'),
(206, 1, 1, 'WAAR de heil\'gen, samenstromend,'),
(206, 1, 2, 'Tot de Boom des levens gaan,'),
(206, 1, 3, 'Waar zij smaken de eeuw\'ge vruchten,'),
(206, 1, 4, 'Daar is onze ziel voldaan.'),
(206, 2, 1, 'Veilig, - waar geen vijand nadert,'),
(206, 2, 2, 'Rust, - het lijden dan voorbij;'),
(206, 2, 3, 'Vrede, - na een weg van strijden;'),
(206, 2, 4, 'Vreugde, - eeuwig Hem nabij.'),
(206, 3, 1, 'Daar zal geen verzoeker wezen,'),
(206, 3, 2, 'En daar is geen zonde meer;'),
(206, 3, 3, 'In Gods paradijs, in vrede,'),
(206, 3, 4, 'Wandelen we in die levenssfeer.'),
(206, 4, 1, 'Daar te drinken van die stromen,'),
(206, 4, 2, 'Door des hemels spijs gevoed:'),
(206, 4, 3, 'Christus, levensvrucht en Gever, -'),
(206, 4, 4, 'Veilig door \'t verlossingsbloed.'),
(206, 5, 1, 'Voorwerp van Gods welgevallen,'),
(206, 5, 2, 'Steeds volmaakt Uw Goddelijk werk!'),
(206, 5, 3, 'Heer der glorie, U zij de ere!'),
(206, 5, 4, 'Vreugde, aanbidding, zonder perk.'),
(207, 1, 1, 'WIJ brengen, God en Vader, U de eer,'),
(207, 1, 2, 'Als zoons met Christus voor Uw aangezicht,'),
(207, 1, 3, 'In heil\'ge liefde en vrijheid, in die sfeer'),
